                <input  class="form-control" type="text" placeholder="Name">
            </div>
            <div class="mb-2">
                <input type="text" class="form-control" placeholder="PhotoUrl">
            </div>
            <div class="mb-2">
                <input type="text" class="form-control" placeholder="Email">
            </div>
            <div class="mb-2">
                <input type="number" class="form-control" placeholder="Mobile">
            </div>
            <div class="mb-2">
                <input type="text" class="form-control" placeholder="Company">
            </div>
            <div class="mb-2">
                <input type="text" class="form-control" placeholder="Title">
            </div>
            <div class="mb-2">
                <select class="form-control">
                    <option value="">Select Group</option>
                </select>
            </div>
            <div class="mb-2">
                <input type="submit" class="btn btn-danger" value="Update">
                <a  class="btn btn-dark ms-2" routerLink="/" >Close</a>
            </div>
            </form>
        </div>
        <div class="col sm-4">
            <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSyXg2UqCRmHe2fWkC52h5JYwA_afK0NloBOWOXCDU&s" class="contact-img">
